import React, { useRef } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowRight } from "@fortawesome/free-solid-svg-icons/faArrowRight";
import Button from "./button";
import { faAdd, faCirclePlus, faGear } from "@fortawesome/free-solid-svg-icons";
import Table from "./table";
import "../styles/create.css";
import { useSelector } from "react-redux";
import { useFileChange } from "../hooks/useFileChange";

const CustomersTab = () => {
  const fileInputRef = useRef(null);
  const onFileChange = useFileChange();

  const uploadMessage = useSelector((state) => state.upload.uploadMessage);
  const tableData = useSelector((state) => state.upload.tableData);

  // Define Create-specific columns
  const customerColumns = [
    { label: "Serial Number", key: "invoiceId" },
    { label: "Customer Name", key: "customerName" },
    { label: "Phone Number", key: "phnNumber" },
    { label: "Company Name", key: "cmpName" },
    { label: "Email", key: "email" },
    { label: "Amount", key: "Amount" },
    { label: "Total Amount", key: "totalamount" },
    { label: "Date", key: "date" },
  ];

  // Transform table data for Create tab
  const customerTableData = tableData.map((item,index) => {
    const isMissing = !item.CustomerName || !item.PhoneNumber || !item.CompanyName || !item.Email
    || !item.TotalAmount || !item.AmountPayable || !item.Date ;
    return {
    invoiceId: index+1 || "N/A",
    customerName: item.CustomerName,
    phnNumber: item.PhoneNumber,
    cmpName: item.CompanyName,
    email: item.Email,
    Amount: item.TotalAmount,
    totalamount: item.AmountPayable,
    date: item.Date,
    isMissing,
    }
  });

  const handleFileUpload = () => {
    if (fileInputRef.current) {
      fileInputRef.current.click();
    }
  };

  return (
    <div className="create">
      <div className="create-nav">
        <div className="title">Create Invoice</div>
        <div className="action-buttons">
          <Button text={"Save as Draft"} className="button-secondary" />
          <Button text={"Save and Print"} className="button-secondary" />
          <Button
            text={"Save"}
            icon={<FontAwesomeIcon icon={faArrowRight} />}
            className="button-primary"
          />
        </div>
      </div>
      <div className="create-subheader">
        <Button
          text={"Add new Invoice"}
          icon={<FontAwesomeIcon icon={faAdd} />}
          className="button-primary"
          onClick={handleFileUpload}
        />
        <input
          type="file"
          ref={fileInputRef}
          style={{ display: "none" }} // Hide the input
          onChange={onFileChange}
        />
        <div>
          <Button
            text={"Custom Headers"}
            icon={<FontAwesomeIcon icon={faCirclePlus} />}
            className="button-primary"
          />
          <Button
            text={"Settings"}
            icon={<FontAwesomeIcon icon={faGear} />}
            className="button-primary"
          />
        </div>
      </div>
      <div className="create-body">
        {console.log(tableData, "Create table data log")}
        {tableData.length > 0 ? (
          <Table tabledata={customerTableData} columns={customerColumns} />
        ) : (
          <div>No data available</div>
        )}
        {uploadMessage && <div className="upload-message">{uploadMessage}</div>}
      </div>
    </div>
  );
};

export default CustomersTab;
