import React, { useRef } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowRight } from "@fortawesome/free-solid-svg-icons/faArrowRight";
import Button from "./button";
import { faAdd, faCirclePlus, faGear } from "@fortawesome/free-solid-svg-icons";
import Table from "./table";
import "../styles/create.css";
import { useSelector } from "react-redux";
import { useFileChange } from "../hooks/useFileChange";
import Tab from "./tab";

const ProductsTab = () => {
//   const fileInputRef = useRef(null);
//   const onFileChange = useFileChange();

//   // Redux states
//   const uploadMessage = useSelector((state) => state.upload.uploadMessage);
  const tableData = useSelector((state) => state.upload.tableData);

  // Define Products-specific columns
  const productColumns = [
    { label: "Serial Number", key: "invoiceId" },
    { label: "Product Name", key: "productName" },
    { label: "Qty", key: "Qty" },
    { label: "Unit Price", key: "unitPrice"},
    { label: "Tax", key: "Tax" },
    { label: "Amount", key: "Amount" },
    { label: "Price with Tax", key: "PriceTax" },
  ];

  // Transform table data for Create tab
  const productTableData = tableData.map((item,index) => ({
    invoiceId: index+1 || "N/A",
    productName: item.ProductName,
    Qty: item.Quantity,
    unitPrice: item.UnitPrice,
    Tax: item.Tax,
    Amount: item.TotalAmount,
    PriceTax: item.AmountPayable,
  }));

//   const handleFileUpload = () => {
//     if (fileInputRef.current) {
//       fileInputRef.current.click();
//     }
//   };

  return (
    <Tab 
    buttonText={"Add Products"}
    columns={productColumns}
    tabledata={productTableData}
    />
  );
};

export default ProductsTab;
