import React, {useRef,useState} from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowRight } from '@fortawesome/free-solid-svg-icons/faArrowRight';
import Button from './button';
import { faAdd, faCirclePlus, faGear } from '@fortawesome/free-solid-svg-icons';
import Table from './table';
import "../styles/create.css"

const Create = () => {

  const fileInputRef = useRef(null);
  const [uploadMessage, setUploadMessage] = useState('');


  const handleFileUpload = () => {
    if (fileInputRef.current) {
      fileInputRef.current.click(); // Programmatically click the hidden input

      setTimeout(() => {
        setUploadMessage(`File "${file.name}" uploaded successfully!`);
      }, 1000); // Simulate delay for upload
    }
  };

  const onFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      console.log("File selected:", file);
      // Handle the uploaded file (e.g., send it to a server or process it)
    }
  };


  return (
    <div className='create'>
      <div className='create-nav'>
        <div className='title'>Create Invoice</div>
        <div className='action-buttons'>
        <Button text={"Save as Draft"} className="button-secondary" />
        <Button text={"Save and Print"} className="button-secondary" />
        <Button text={"Save"} icon={<FontAwesomeIcon icon={faArrowRight} />} className="button-primary"  />
        </div>
      </div>
      <div className='create-subheader'>
        <Button text={"Add new Invoice"} icon={<FontAwesomeIcon icon={faAdd} />} className="button-primary" 
        onClick={()=>{handleFileUpload()}}/>
        <input
          type="file"
          ref={fileInputRef}
          style={{ display: 'none' }} // Hide the input
          onChange={onFileChange}
        />
        <div>
        <Button text={"Custom Headers"} icon={<FontAwesomeIcon icon={faCirclePlus} />} className="button-primary" />
        <Button text={"Settings"} icon={<FontAwesomeIcon icon={faGear} />} className="button-primary" />
        </div>
      </div>
      <div className='create-body'>
        <Table />
      </div>
    </div>
  )
}

export default Create
