import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCheckCircle, faAdd, faHeadset, faVideo, faCirclePlay, faGear } from '@fortawesome/free-solid-svg-icons'; 
import Button from './button';
import infoImg from '../images/sales.webp';
import '../styles/card.css';
import CardHeader from './cardHeader';

const Card = () => {
    const infoData = [
        { text: "Customizable templates", icon: faCheckCircle },
        { text: "GST reports", icon: faCheckCircle },
        { text: "Premium support", icon: faCheckCircle },
      ];

  return (
    <div className='card'>
    <CardHeader />
      <img src={infoImg} alt='card' className='info-img' />
      <div className='card-body'>
        <h2 className='header'>Creating invoices lightning fast.</h2>
        <p className='sub-header'>Get a detailed summary of all your sales transactions.</p>
            {infoData.map((item, index) => (
                <div key={index} className="info-item">
                <FontAwesomeIcon icon={item.icon} className="info-icon" />
                <span>{item.text}</span>
                </div>
            ))}
        <Button text={"Create your 1st invoice"} icon={<FontAwesomeIcon icon={faAdd} />} />
        <div className='info-item'><FontAwesomeIcon icon={faHeadset} /> <span>Talk to specialist</span></div>
        <div className='info-item'><FontAwesomeIcon icon={faVideo} /> <span>Watch a quick video here</span></div>
      </div>
    </div>
  );
};

export default Card;
