import React, { useRef } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowRight } from "@fortawesome/free-solid-svg-icons/faArrowRight";
import Button from "./button";
import { faAdd, faCirclePlus, faGear } from "@fortawesome/free-solid-svg-icons";
import Table from "./table";
import "../styles/create.css";
import { useSelector } from "react-redux";
import { useFileChange } from "../hooks/useFileChange";

const ProductsTab = () => {
  const fileInputRef = useRef(null);
  const onFileChange = useFileChange();

  // Redux states
  const uploadMessage = useSelector((state) => state.upload.uploadMessage);
  const rawTableData = useSelector((state) => state.upload.tableData);

  // Define Products-specific columns
  const productColumns = [
    { label: "Product Name", key: "productName" },
    { label: "Price", key: "price" },
    { label: "Quantity", key: "quantity" },
    { label: "Total", key: "total" },
  ];

  // Transform table data for Products tab
  const productTableData = rawTableData.map((item) => ({
    productName: item.name || "N/A",
    price: item.price || 0,
    quantity: item.quantity || 1,
    total: (item.price || 0) * (item.quantity || 1),
  }));

  const handleFileUpload = () => {
    if (fileInputRef.current) {
      fileInputRef.current.click();
    }
  };

  return (
    <div className="create">
      <div className="create-nav">
        <div className="title">Products</div>
        <div className="action-buttons">
          <Button text={"Save as Draft"} className="button-secondary" />
          <Button text={"Save and Print"} className="button-secondary" />
          <Button
            text={"Save"}
            icon={<FontAwesomeIcon icon={faArrowRight} />}
            className="button-primary"
          />
        </div>
      </div>
      <div className="create-subheader">
        <Button
          text={"Add new Product"}
          icon={<FontAwesomeIcon icon={faAdd} />}
          className="button-primary"
          onClick={handleFileUpload}
        />
        <input
          type="file"
          ref={fileInputRef}
          style={{ display: "none" }} // Hide the input
          onChange={onFileChange}
        />
        <div>
          <Button
            text={"Custom Headers"}
            icon={<FontAwesomeIcon icon={faCirclePlus} />}
            className="button-primary"
          />
          <Button
            text={"Settings"}
            icon={<FontAwesomeIcon icon={faGear} />}
            className="button-primary"
          />
        </div>
      </div>
      <div className="create-body">
        {console.log(productTableData, "Product table data log")}
        {productTableData.length > 0 ? (
          <Table tabledata={productTableData} columns={productColumns} />
        ) : (
          <div>No product data available</div>
        )}
        {uploadMessage && <div className="upload-message">{uploadMessage}</div>}
      </div>
    </div>
  );
};

export default ProductsTab;
